import React from "react";
import ReactDOM from "react-dom";

const Header = props => <h1>{props.courseName}</h1>;

const Part = props => (
  <p>
    {props.partName} {props.partExercises}
  </p>
);

const Content = ({ parts }) => (
  <div>
    {parts.map(part => <Part partName={part.name} partExercises={part.exercises} />)}
  </div>
);

const Total = props => (
  <p>
    yhteensä {props.exercises[0] + props.exercises[1] + props.exercises[2]}{" "}
    tehtävää
  </p>
);

const Course = ({ course }) => (
  <div>
    <Header courseName={course.name} />
    <Content parts={course.parts} />
    <Total exercises={course.parts.map(p => p.exercises)} />
  </div>
);

const App = () => {
  const course = {
    name: "Half Stack -sovelluskehitys",
    parts: [
      {
        name: "Reactin perusteet",
        exercises: 10
      },
      {
        name: "Tiedonvälitys propseilla",
        exercises: 7
      },
      {
        name: "Komponenttien tila",
        exercises: 14
      }
    ]
  };

  return (
    <div>
      <Course course={course} />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
