import React from "react";
import countryService from "../services/countryService";

const Country = ({ country }) => {
  const weatherInCapital = countryService
    .getCapitalWeather(country.capital)
    .then(weather => weather.current);
  console.log(weatherInCapital)
  return (
    <div>
      <h1>{country.name}</h1>
      <p>capital {country.capital}</p>
      <p>population {country.population}</p>
      <h2>languages</h2>
      <ul>
        {country.languages.map(lang => (
          <li key={lang.name}>{lang.name}</li>
        ))}
      </ul>
      <img
        src={country.flag}
        height="20%"
        width="20%"
        alt="the country's flag"
      />
      <h2>Weather in {country.capital}</h2>
      <p>temperature {weatherInCapital.temp_c}</p>
      <img src={weatherInCapital.condition.icon.substr(2)} alt={weatherInCapital.condition.text} />
    </div>
  );
};

export default Country;
